from selenium import webdriver
from selenium.webdriver.common.by import By
import time
timeout = time.time() + 60*5

Save = "%3D%21END%21"

chrome_options = webdriver.ChromeOptions()
chrome_options.add_experimental_option("detach", True)

driver = webdriver.Chrome(options=chrome_options)
driver.get(url="https://orteil.dashnet.org/experiments/cookie/")

cookie = driver.find_element(By.ID, "cookie")
upgrades = ["Time machine", "Portal", "Alchemy lab", "Shipment", "Mine", "Factory", "Grandma"]
count = 0
money = 0


def update_money():
    global money
    money = driver.find_element(By.ID, "money").text
    money = int(money.replace(',', ''))


update_money()
while True:
    update_money()
    time.sleep(0.01)
    count += 0.01
    cookie.click()

    if count >= 5:
        count = 0
        for upgrade in upgrades:
            update_money()

            item = driver.find_element(By.ID, f"buy{upgrade}")
            price = item.find_element(By.XPATH, f'//*[@id="buy{upgrade}"]/b').text.split('-')[1].strip()
            price = int(price.replace(',', ''))

            if price <= money:
                item.click()
                update_money()

    if time.time() > timeout:
        print(driver.find_element(By.ID, "cps").text)
        driver.quit()
        break
